import axios from "axios";
import {React,useState} from "react";
import { useNavigate } from "react-router";

import './assests/css/main.css';
import './assests/css/main.css.map';
import logo from './assests/images/tractor.jpg';


export default function Fn(){

  const navigate=useNavigate();


const [login,setLogin]=useState({
  mail:"",
  pass:""
})

function handleChange(event){
  const {name,value}=event.target;
  setLogin((preState)=>{
    return{
      ...preState,
      [name]:value,
    }
  })
  sessionStorage.setItem('mail',login.mail);
  }

  const{mail,pass}=login;


function handleSubmit(){

  let serverCheck="";

  axios
  .post('/clientCheck',{
    'mail':mail
  })
  .then((res)=>{
    console.log(res.data[0].password)
    serverCheck=res.data[0].password;
  })
  if(pass===serverCheck){
    navigate('/');
  }
  else{
    alert("Password Thappu da thambi!!!游때游때游때");
  }
  console.log(serverCheck);



}





  return(
    <>
    <div className="container">
      <img className="mx_img" src={logo} alt="" />
      <div className="log">
        <form onSubmit={handleSubmit} >
          <div className="log-card">
          <p>Username:</p>
          <input
           type="text"
           name="mail"
           onChange={handleChange}
           value={mail}
           
           />
          <p>Password</p>
          <input type="text"
          name="pass"
          onChange={handleChange}
          value={pass}
          
          />

          <button>Login</button>
        </div>
        </form>

      </div>
    </div>
    </>
  );

}
