import React from "react";
import { Link } from "react-router-dom";
import { useState } from "react";


import './assests/css/main.css';
import './assests/css/main.css.map';
import "./assests/js/main.js";
import './assests/scss/_layouts/_header.scss';
import './assests/scss/_layouts/_navigation.scss';
import './assests/scss/abstract/_font.scss';
import './assests/scss/abstract/_variables.scss';
import './assests/scss/components/_switch.scss';
import './assests/scss/forms/login.scss';
import './assests/scss/forms/reg.scss';
import './assests/scss/main.scss';
import img1 from './assests/images/search.png';
import axios from "axios";



export default function RegExp(){


  const [farmer , setFarmer]=useState({
    name:"",
    phnum:"",
    mail:"",
    tempass:"",
    pass:"",
    pic:"",
    picname:''
  });


  function change(event){
  //   console.log(event.target.files[0].name);
  //   let render=new FileReader();
  //   render.readAsDataURL(event.target.files[0]);
  //   console.log(event.target.value);
  //  render.onload = () => {
  //   localStorage.setItem('namw',render.result)
  //   console.log(render.result)
    setFarmer((preState)=>{
      return{
        ...preState,
       pic:event.target.files[0],
      };
    })

    setFarmer((preState)=>{
      return{
        ...preState,
       picname:event.target.files[0].name,
      };
    })

  };
  //localStorage.setItem('namw',render.result);

  


  function handleChange(event){
    const{name , value}=event.target;
  
    setFarmer((preState)=>{
      return{
        ...preState,
       [name]:value,
      };
    })
  
  }


  const {name ,tempass, phnum, mail,pass,pic,picname}=farmer;



  function sub(){
    const formdata=new FormData();
    formdata.append('name',name);
    formdata.append('phnum',phnum);
    formdata.append('mail',mail);
    formdata.append('pass',pass);
    formdata.append('pic',pic);
    formdata.append('picname',picname);
  
    axios
      .post('/farmerstore',formdata)
      //.then((res)=>setMessage(res.data))
      .catch((err)=>{
      console.log(err)});

  }


  // function handleSubmit(){
  //   alert("!!!  BabeðŸ˜ðŸ˜ðŸ˜ðŸ˜ðŸ’šðŸ’šðŸ’•ðŸ’šðŸ’š Register panita ne...!")

  //   fetch("/farmerstore",
  //   {
  //     method:'POST',
  //     body: JSON.stringify(farmer),
  //     headers:{ "Content-type": "application/json; charset=UTF-8" }

  //   })
  //   //.then((res)=> alert(res));
  //   .then(res => res.json()) .then(json => console.log(json));
    
    
  // }


    return(
        <>
        <nav className="navbar">
        <div className="logo">
          <a href="">LOGO</a>
        </div>
        <ul className="navbar--items">
          <li className="navbar--links">
            <a href="./index.html">Home</a>
          </li>
          <li className="navbar--links">
            <a href="#">About</a>
          </li>
          <li className="navbar--links">
            <a href="#">Contact</a>
          </li>
          <li className="navbar--links sign">
            <Link to='/login' className="btn1">Sign In</Link>
          </li>
        </ul>
        <div className="search">
          <input className="search--tbox" type="text" />
          <button className="search--btn">
          {/* style="width: 30px" */}
            <img src={img1} alt="" className='oneim' />
          </button>
        </div>
      </nav>
      <div className="reg">
        <form autoComplete="on" className="reg-cont" action="#" onSubmit={sub} encType="multipart/form-data">
          <p className="reg-tit">REGISTER</p>
          <div className="reg-ele">
            <p>Name</p>
            <input
              className="reg-txt"
              type="text"
              name="name"
              placeholder="Type here"
              onChange={handleChange}
              value={name}
              required
            />
          </div>
          <div className="reg-ele">
            <p>Phone number</p>
            <input
              className="reg-txt"
              type="text"
              name="phnum"
              onChange={handleChange}
              value={phnum}
              placeholder={1234567890}
              required
            />
          </div>
          <div className="reg-ele">
            <p>Mail ID</p>
            <input
              className="reg-txt"
              type="text"
              name="mail"
              placeholder="Type here"
              onChange={handleChange}
              value={mail}
              required
            />
          </div>
          <div className="reg-ele">
            <p>Gender</p>
            <input type="radio" id="gm" name="g_radio" value="Male" required />
            <label >Male</label>
            <input type="radio" id="gf" name="g_radio" value="Female" required />
            <label >Female</label>
          </div>
          <div className="reg-ele">
            <p>Password</p>
            <input
              className="reg-txt"
              type="password"
              name="tempass"
              placeholder="Type here"
              onChange={handleChange}
              value={tempass}
              required
            />
          </div>
          <div className="reg-ele">
            <p>Confirm Password</p>
            <input
              className="reg-txt"
              type="password"
              name="pass"
              placeholder="Type here"
              onChange={handleChange}
              value={pass}
             // required

            />
            <input
            type="File"
            onChange={change}
            name="pic"
            />
          </div>
          <button className="reg-btn">SUBMIT</button>
        </form>
      </div>
        </>
    );

}
