import { Outlet,Link } from 'react-router-dom';
import React from 'react';

 export default class NameForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {valueee: ' ',pass:''};
    this.handleChange = this.handleChange.bind(this); 
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(e) {
    console.log(e.target.name);
    if(e.target.name==='pass')
    this.setState({pass: e.target.value});
    this.setState({valueee: e.target.value});
  //   this.setState({[e.target.name]: e.target.value,
  // });
}
  handleSubmit = (event) => {
    // const { bookID, bookTitle } = this.state;

    // const book = {
    //   bookID,
    //   bookTitle,
    // };
     console.log(this.state);
    fetch("/express_backend", 
      {
        method: 'POST',
        body: JSON.stringify(this.state),
        headers: { "Content-type": "application/json; charset=UTF-8" }
      })
      .then(res => res.json()) .then(json => console.log(json));
  }
  /*
  UNSAFE_componentWillMount() {
    this.callBackendAPI()
      .then(res => this.setState({ value: res.values}))
  }

  callBackendAPI = async () => {
    const response = await fetch('/jana');
    const body = await response.json();

    if (response.status !== 200) {
      throw Error(body.message) 
    }
    return body;
  };
  */

  render() {
    return (
      <>
      <form action="#" onSubmit={this.handleSubmit}>
          Name:<input type="text" name='pass' onChange={this.handleChange} /> <br/> <br/>
          <input type="text" name='valueee'onChange={this.handleChange} /> <br/> <br/>
          <p>Text: {this.state.value}</p>
          <button type='submit'>Submit</button>
      </form>
  
       
      {/* <nav>
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          <li>
            <Link to="/Layout">Blogs</Link>
          </li>
          <li>
            <Link to="/Sample">Contact</Link>
          </li>
        </ul>
      </nav> */}

      <Outlet />


      </>
    );
  }
}
