    //   };
    // })
    //console.log(picname[9]);
    if(pass===res.data[0].password){
      navigate('/Register');
  }
  else{
    console.log(serverCheck);
    console.log(pass);
    alert("Password Thappu da thambi!!!游때游때游때");
  }
   
  })
  .catch((err)=>{
  console.log(err)});

