  //   .then(res => res.json()) .then(json => console.log(json.data[0].password));



}





  return(
    <>
    <div className="container">
      <img className="mx_img" src={logo} alt="" />
      <div className="log">
        <form  >
          <div className="log-card">
          <p>Username:</p>
          <input
           type="text"
           name="mail"
           onChange={handleChange}
           value={mail}
           
           />
          <p>Password</p>
          <input type="text"
          name="pass"
          onChange={handleChange}
          value={pass}
          
          />

          <button onClick={()=>check()}>Login</button>
        </div>
        </form>

      </div>
    </div>
    </>
  );

}
