import React from "react";


export default class NameForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {value: '  ',text:' '};
    this. handleChange = this.handleChange.bind(this); 
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(event) {
    this.setState({
      [event.target.name]:event.target.value,
    });
    
  }
  handleSubmit = (event) => {
    
    
    const { bookID, bookTitle } = this.state;

    const book = {
      bookID,
      bookTitle,
    };

    axios
    .post('http://localhost:3001/express_backend', book)
    .then(() => console.log('Book Created'))
    .catch(err => {
      console.error(err);});


    // fetch("/express_backend",{
    //     method: 'POST',
    //     body: JSON.stringify(this.book),
    //     headers: { "Content-type": "application/json; charset=UTF-8" }
    //   })
    //   .then(res => res.json()) .then(json => console.log(json));
  }
  /*
  UNSAFE_componentWillMount() {
    this.callBackendAPI()
      .then(res => this.setState({ value: res.values}))
  }

  callBackendAPI = async () => {
    const response = await fetch('/jana');
    const body = await response.json();

    if (response.status !== 200) {
      throw Error(body.message) 
    }
    return body;
  };
  */

  render() {
    return (
      <>
      <form action="#" onSubmit={this.handleSubmit}>
          Name:<input type="text" name ="value" onChange={this.handleChange} /> <br/> <br/>
          <input type="text" name="text" onChange={this.gopass} /><br></br>
          <p>Text: {this.state.value}</p>
          <button type='submit'>Submit</button>
      </form>
      </>
    );
  }
}
