import React from "react";

class NameForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {value: '  '}; //useState Initalization 
        this.handleChange = this.handleChange.bind(this); //Event Handling 
        this.handleSubmit = this.handleSubmit.bind(this);
      }
    
      handleChange(event) {
        this.setState({value: event.target.value});
      }
    
      handleSubmit = (event) => {
        fetch("/express_backend", 
          {
            method: 'POST',
            body: JSON.stringify(this.state),
            headers: {
              "Content-type": "application/json; charset=UTF-8"
          }
          })
          .then(res => res.json()) .then(json => console.log(json));
      }
      UNSAFE_componentWillMount() {
        //Setting state here will trigger re-rendering.
        this.callBackendAPI()
          .then(res => this.setState({ value: res.values}))
      }
      callBackendAPI = async () => {
        const response = await fetch('/jana');
        const body = await response.json();
    
        if (response.status !== 200) {
          throw Error(body.message) 
        }
        return body;
      };
      render() {
        return (
          <>
          <form action="#" onSubmit={this.handleSubmit}>
              Name:<input type="text" onChange={this.handleChange} /> <br/> <br/>
              <p>Text: {this.state.value}</p>
              <button type='submit'>Submit</button>
          </form>
          </>
        );
      }
  }
  
export default NameForm;
